import React, { useEffect } from "react";
import Navbar from "../Components/Navbar";
import { Outlet, useLocation, useNavigate } from "react-router-dom";
import Action from "./Action";
import { useUser } from "../Components/UserContext";

const Home = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const { userDetails, setUserDetails } = useUser(); // This line might be causing the issue

  useEffect(() => {
    if (!sessionStorage.getItem("token")) {
      navigate("/");
    } else {
      setUserDetails(location.state ? location.state.data : null);
    }
  }, [location.state, navigate, setUserDetails]);

  const username = userDetails
    ? userDetails.email.match(/^([A-Za-z]+)/)[1]
    : null;

  return (
    <>
      <Navbar name={username}></Navbar>
      <Action></Action>
      <Outlet></Outlet>
    </>
  );
};

export default Home;
