import React, { useState } from "react";
import { Button, Modal, TextField, Typography } from "@mui/material";

const AddContactModal = ({ open, onClose, onAddContact }) => {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [phoneNumber, setPhoneNumber] = useState("");
  const [photo, setPhoto] = useState(null);

  const handleAddContact = () => {
    // Validate form fields (you can add your validation logic here)

    // Call the onAddContact function with the contact data
    onAddContact({ name, email, phoneNumber, photo });

    // Clear form fields
    setName("");
    setEmail("");
    setPhoneNumber("");
    setPhoto(null);

    // Close the modal
    onClose();
  };

  const handlePhotoChange = (e) => {
    const file = e.target.files[0];
    setPhoto(file);
  };

  return (
    <Modal open={open} onClose={onClose}>
      <div style={{ position: "absolute", top: "50%", left: "50%", transform: "translate(-50%, -50%)", width: 400, backgroundColor: "white", padding: 20 }}>
        <Typography variant="h5" gutterBottom>Add Contact</Typography>
        <TextField label="Name" variant="outlined" fullWidth value={name} onChange={(e) => setName(e.target.value)} style={{ marginBottom: 10 }} />
        <TextField label="Email" variant="outlined" fullWidth value={email} onChange={(e) => setEmail(e.target.value)} style={{ marginBottom: 10 }} />
        <TextField label="Phone Number" variant="outlined" fullWidth value={phoneNumber} onChange={(e) => setPhoneNumber(e.target.value)} style={{ marginBottom: 10 }} />
        <input type="file" accept="image/*" onChange={handlePhotoChange} style={{ marginBottom: 10 }} />
        <Button variant="contained" color="primary" onClick={handleAddContact} style={{ marginRight: 10 }}>Add Contact</Button>
        <Button variant="contained" onClick={onClose}>Cancel</Button>
      </div>
    </Modal>
  );
};

export default AddContactModal;
