import * as React from "react";
import Avatar from "@mui/material/Avatar";
import Button from "@mui/material/Button";
import CssBaseline from "@mui/material/CssBaseline";
import TextField from "@mui/material/TextField";
import { Link } from "react-router-dom";
import Grid from "@mui/material/Grid";
import Box from "@mui/material/Box";
import LockOutlinedIcon from "@mui/icons-material/LockOutlined";
import Typography from "@mui/material/Typography";
import Container from "@mui/material/Container";
import { createTheme, ThemeProvider } from "@mui/material/styles";
import { useForm } from "react-hook-form";
import { useNavigate } from "react-router-dom";
import { getUsersInStorage, setSession } from "../Services/Storage";
import Snackbar from "@mui/material/Snackbar";
import Alert from "@mui/material/Alert";
import Slide from "@mui/material/Slide";
import { useState } from "react";
import { useEffect } from "react";

function Copyright(props) {
  return (
    <Typography
      variant="body2"
      color="text.secondary"
      align="center"
      {...props}
    >
      {"Copyright © "}
      <Link color="inherit" to="https://ritiksinghrajput.netlify.app/">
        Ritik Singh
      </Link>{" "}
      {new Date().getFullYear()}
      {"."}
    </Typography>
  );
}
let encodedPassword;
const defaultTheme = createTheme();

export default function SignIn() {
  const navigate = useNavigate();
  useEffect(() => {
    if (sessionStorage.getItem("activeUserId")) {
      navigate("home");
    }
  }, [navigate]);
  const [open, setSnackBarOpen] = useState(false);
  const vertical = "top";
  const horizontal = "right";
  const {
    register,
    handleSubmit,
    setError,
    formState: { errors, isSubmitting },
  } = useForm();

  function handleNavigate() {
    navigate("home");
  }
  function encrypt(data) {
    const baseString = data.password;
    encodedPassword = window.btoa(baseString);
    return encodedPassword;
  }
  const onSubmit = async (data) => {
    await new Promise((resolve) => setTimeout(resolve, 1000));
    try {
      const formData = { ...data };
      formData.password = encrypt(data);
      const encryptedData = { ...formData };
      const users = getUsersInStorage();
      if (users.length === 0 || users == null) {
        setError("root", {
          message: "Email not registered please sign up",
        });
      }

      users.forEach((element) => {
        if (
          element.email === encryptedData.email &&
          element.password === encryptedData.password
        ) {
          setSession(element.userId);
          setSnackBarOpen(true);
          setTimeout(handleNavigate, 1000);
        } else {
          setError("root", {
            message: "No user found with this email or password",
          });
        }
      });
      throw new Error();
    } catch (error) {
      console.log(error.message);
    }
  };
  const handleClose = (event, reason) => {
    if (reason === "click-away") {
      return;
    }
    setSnackBarOpen(false);
  };

  function TransitionLeft(props) {
    return <Slide {...props} direction="left" />;
  }
  return (
    <>
      <Snackbar
        open={open}
        autoHideDuration={3000}
        onClose={handleClose}
        TransitionComponent={TransitionLeft}
        anchorOrigin={{ vertical, horizontal }}
      >
        <Alert onClose={handleClose} severity="success" sx={{ width: "100%" }}>
          Login Successful!
        </Alert>
      </Snackbar>
      <ThemeProvider theme={defaultTheme}>
        <Container component="main" maxWidth="xs">
          <CssBaseline />
          <Box
            sx={{
              marginBottom: 8,
              marginTop: 8,
              display: "flex",
              flexDirection: "column",
              alignItems: "center",
            }}
          >
            <Avatar sx={{ m: 1, bgcolor: "secondary.main" }}>
              <LockOutlinedIcon />
            </Avatar>
            <Typography component="h1" variant="h5">
              Sign in
            </Typography>
            <Box
              component="form"
              onSubmit={handleSubmit(onSubmit)}
              noValidate
              sx={{ mt: 1 }}
            >
              <TextField
                {...register("email", {
                  required: "Email is required",
                  validate: (value) => {
                    if (!value.match(/^\S+@\S+\.\S+$/)) {
                      return "Email is invalid";
                    } else {
                      return true;
                    }
                  },
                })}
                margin="normal"
                fullWidth
                required
                id="email"
                label="Email Address"
                name="email"
                autoComplete="email"
                autoFocus
              />
              <div></div>
              {errors.email && (
                <div className="error">{errors.email.message}</div>
              )}
              <TextField
                {...register("password", {
                  required: "Password is required",
                })}
                margin="normal"
                required
                fullWidth
                name="password"
                label="Password"
                type="password"
                id="password"
                autoComplete="current-password"
              />
              <div></div>
              {errors.password && (
                <div className="error">{errors.password.message}</div>
              )}
              <Button
                type="submit"
                fullWidth
                variant="contained"
                sx={{ mt: 3, mb: 2 }}
                disabled={isSubmitting}
              >
                {isSubmitting ? "Loading..." : "Sign In"}
              </Button>
              {errors.root && (
                <div className="error">{errors.root.message}</div>
              )}
              <Grid container>
                <Grid item xs>
                  <Link href="#" variant="body2"></Link>
                </Grid>
                <Grid item>
                  <Link to="register" variant="body2">
                    {"Don't have an account? Sign Up"}
                  </Link>
                </Grid>
              </Grid>
            </Box>
          </Box>
          <Copyright sx={{ mt: 8, mb: 4 }} />
        </Container>
      </ThemeProvider>
    </>
  );
}
