import * as React from "react";
import { styled } from "@mui/material/styles";
import Table from "@mui/material/Table";
import TableBody from "@mui/material/TableBody";
import TableCell, { tableCellClasses } from "@mui/material/TableCell";
import TableContainer from "@mui/material/TableContainer";
import TableHead from "@mui/material/TableHead";
import TableRow from "@mui/material/TableRow";
import Paper from "@mui/material/Paper";
import Avatar from "@mui/material/Avatar";

const StyledTableCell = styled(TableCell)(({ theme }) => ({
  [`&.${tableCellClasses.head}`]: {
    backgroundColor: theme.palette.common.black,
    color: theme.palette.common.white,
  },
  [`&.${tableCellClasses.body}`]: {
    fontSize: 14,
  },
}));

const StyledTableRow = styled(TableRow)(({ theme }) => ({
  "&:nth-of-type(odd)": {
    backgroundColor: theme.palette.action.hover,
  },
  // hide last border
  "&:last-child td, &:last-child th": {
    border: 0,
  },
}));

function createData(avatar, name, email, number, action, contactId) {
  return { avatar, name, email, number, action, contactId };
}

const rows = JSON.parse(localStorage.getItem("contacts")) || [];

const displayRows = rows.map((row) => {
  let avatarComponent;
  if (row.avatar) {
    // Check if the avatar field contains a Base64 string
    if (row.avatar.startsWith("data:image")) {
      // If it's a Base64 string, directly use it as the src attribute
      avatarComponent = <Avatar alt={row.name} src={row.avatar} />;
    } else {
      // Otherwise, use the first letter of the name as a fallback
      avatarComponent = <Avatar>{row.name[0]}</Avatar>;
    }
  } else {
    // If avatar field is empty, use the first letter of the name as a fallback
    avatarComponent = <Avatar>{row.name[0]}</Avatar>;
  }
  return createData(
    avatarComponent,
    row.name,
    row.email,
    row.number,
    "edit delete",
    row.contactId
  );
});

export default function CustomizedTables() {
  return (
    <TableContainer component={Paper}>
      <Table sx={{ minWidth: 600 }} aria-label="customized table">
        <TableHead>
          <TableRow>
            <StyledTableCell>Avatar</StyledTableCell>
            <StyledTableCell align="right">Name</StyledTableCell>
            <StyledTableCell align="right">Number</StyledTableCell>
            <StyledTableCell align="right">Email</StyledTableCell>
            <StyledTableCell align="right">Action</StyledTableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {displayRows.map((row) => (
            <StyledTableRow key={row.contactId}>
              <StyledTableCell component="th" scope="row">
                {row.avatar}
              </StyledTableCell>
              <StyledTableCell align="right">{row.name}</StyledTableCell>
              <StyledTableCell align="right">{row.email}</StyledTableCell>
              <StyledTableCell align="right">{row.number}</StyledTableCell>
              <StyledTableCell align="right">{row.action}</StyledTableCell>
            </StyledTableRow>
          ))}
        </TableBody>
      </Table>
    </TableContainer>
  );
}
