import React from "react";
import { NavLink, useRoutes } from "react-router-dom";
import { useLocation } from "react-router-dom";

const Action = () => {
  const location = useLocation();
  const { pathname } = location;
  const splitLocation = pathname.split("/");
  return (
    <>
      <div className="container">
        <NavLink
          to="add"
          className={splitLocation[1] === "add" ? "active" : ""}
        >
          Add Contact
        </NavLink>
        <NavLink
          to="view"
          className={splitLocation[1] === "view" ? "active" : ""}
        >
          View Contact
        </NavLink>
        <NavLink to="import">Import Contact</NavLink>
        <NavLink to="export">Export Contact</NavLink>
      </div>
    </>
  );
};

export default Action;
