import React, { useState } from "react";

const AddContactModal = ({ onClose, onAddContact }) => {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [phoneNumber, setPhoneNumber] = useState("");
  const [photo, setPhoto] = useState(null);

  const handleAddContact = () => {
    // Validate form fields (you can add your validation logic here)

    // Call the onAddContact function with the contact data
    onAddContact({ name, email, phoneNumber, photo });

    // Clear form fields
    setName("");
    setEmail("");
    setPhoneNumber("");
    setPhoto(null);

    // Close the modal
    onClose();
  };

  const handlePhotoChange = (e) => {
    const file = e.target.files[0];
    setPhoto(file);
  };

  return (
    <div className="modal">
      <div className="modal-content">
        <span className="close" onClick={onClose}>&times;</span>
        <h2>Add Contact</h2>
        <form>
          <div>
            <label>Name:</label>
            <input type="text" value={name} onChange={(e) => setName(e.target.value)} />
          </div>
          <div>
            <label>Email:</label>
            <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} />
          </div>
          <div>
            <label>Phone Number:</label>
            <input type="tel" value={phoneNumber} onChange={(e) => setPhoneNumber(e.target.value)} />
          </div>
          <div>
            <label>Photo:</label>
            <input type="file" accept="image/*" onChange={handlePhotoChange} />
          </div>
          <button type="button" onClick={handleAddContact}>Add Contact</button>
        </form>
      </div>
    </div>
  );
};

export default AddContactModal;
