import React, { useEffect, useState } from "react";
import Navbar from "../Components/Navbar";
import { Outlet, useLocation, useNavigate } from "react-router-dom";
import Action from "./Action";
import AddContactModal from "../Pages/AddContact";

const Home = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const [showModal, setShowModal] = useState(false);

  useEffect(() => {
    if (!sessionStorage.getItem("token")) {
      navigate("/");
    }
  }, []);

  // Extracting user details if available
  const userDetails = location.state ? location.state.data : null;
  const username = userDetails ? userDetails.email.match(/^([A-Za-z]+)/)[1] : null;

  const handleAddContact = (contactData) => {
    // Handle adding contact logic here
    console.log("Adding contact:", contactData);
  };

  return (
    <>
      <Navbar name={username}></Navbar>
      <Action></Action>
      <Outlet></Outlet>
      {showModal && <AddContactModal onClose={() => setShowModal(false)} onAddContact={handleAddContact} />}
      <button onClick={() => setShowModal(true)}>Add Contact</button>
    </>
  );
};

export default Home;
